  render() {
    return (
      <div>
        <button onClick = { handleClick } > Meu botão </button>
        <button onClick = { handleClick } > Meu botão </button>
        <button onClick = { handleClick } > Meu botão </button>
      </div>
    )
  }
}

export default App;
